import {
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselNext,
  CarouselPrevious,
} from "@/components/ui/carousel";
import { Card, CardContent } from "@/components/ui/card";
import { PlaceHolderImages } from "@/lib/placeholder-images";

export default function StoreShowcase() {
  const shopifyImages = PlaceHolderImages.filter((img) =>
    img.id.startsWith("shopify-store-")
  );

  return (
    <Carousel
      className="w-full max-w-4xl mx-auto"
      opts={{
        align: "start",
        loop: true,
      }}
    >
      <CarouselContent className="-ml-4">
        {shopifyImages.map((image, index) => (
          <CarouselItem key={index} className="pl-4 md:basis-1/2 lg:basis-1/3">
            <div className="p-1">
              <Card className="overflow-hidden group">
                <CardContent className="flex aspect-[4/3] items-center justify-center p-0">
                  <img
                    src={image.imageUrl}
                    alt={image.description}
                    width={800}
                    height={600}
                    className="object-cover w-full h-full transition-transform duration-500 ease-in-out group-hover:scale-105"
                    data-ai-hint={image.imageHint}
                  />
                </CardContent>
              </Card>
            </div>
          </CarouselItem>
        ))}
      </CarouselContent>
      <CarouselPrevious className="hidden sm:flex" />
      <CarouselNext className="hidden sm:flex" />
    </Carousel>
  );
}

src/components/homepage/hero.tsx.content:
import React, { useState } from "react";

import {
  Carousel,
  CarouselContent,
  CarouselItem,
} from "@/components/ui/carousel";
import { PlaceHolderImages } from "@/lib/placeholder-images";
import Autoplay from "embla-carousel-autoplay";

type HeroImage = {
  id: string;
  imageUrl: string;
  description: string;
  imageHint?: string;
};

export default function Hero() {
  const initialImages = PlaceHolderImages.filter((img) =>
    img.id.startsWith("hero-")
  );

  const [images, setImages] = useState<HeroImage[]>(initialImages);

  return (
    <section className="w-full">
      <Carousel
        plugins={[
          Autoplay({
            delay: 2000,
            stopOnInteraction: false,
            playOnInit: true,
          }),
        ]}
        className="w-full"
        opts={{ loop: true }}
      >
        <CarouselContent>
          {images.map((img) => (
            <CarouselItem key={img.id}>
              <div className="relative h-[60vh] md:h-[80vh] w-full">
                <img
                  src={img.imageUrl}
                  alt={img.description}
                  className="w-full h-full object-cover"
                  data-ai-hint={img.imageHint}
                  loading={img.id === "hero-1" ? "eager" : "lazy"}
                />
              </div>
            </CarouselItem>
          ))}
        </CarouselContent>
      </Carousel>
    </section>
  );
}

